package xyz.knightj.my_vocab_vault.controllerimport org.springframework.http.HttpStatusimport org.springframework.http.ResponseEntityimport org.springframework.web.bind.annotation.CrossOriginimport org.springframework.web.bind.annotation.GetMappingimport org.springframework.web.bind.annotation.PathVariableimport org.springframework.web.bind.annotation.PostMappingimport org.springframework.web.bind.annotation.RequestBodyimport org.springframework.web.bind.annotation.RequestHeaderimport org.springframework.web.bind.annotation.RequestMappingimport org.springframework.web.bind.annotation.RestControllerimport xyz.knightj.my_vocab_vault.models.*import xyz.knightj.my_vocab_vault.service.WordManagementServiceimport xyz.knightj.my_vocab_vault.utils.UserUtils@RestController@RequestMapping("/api/word-management")@CrossOrigin(origins = ["http://localhost:5173"])class WordManagementController(private val wordManagementService: WordManagementService) {    @GetMapping("/words")    fun getAllWords(@RequestHeader headers: Map<String, String>): ResponseEntity<List<WordDetails>> {        val userId: String = UserUtils.getCurrentUserId()        val retrievedWords: List<WordDetails> = wordManagementService.findAllWords(userId);        return ResponseEntity.ok().body(retrievedWords)    }    @GetMapping("/word/{word}")    fun getAllWords(@RequestHeader headers: Map<String, String>, @PathVariable("word") wordName: String): ResponseEntity<WordDetails> {        val userId: String = UserUtils.getCurrentUserId()        val retrievedWords: WordDetails = wordManagementService.findByWord(userId, wordName);        return ResponseEntity.ok().body(retrievedWords)    }    @PostMapping("/word")    fun saveWord(@RequestHeader headers: Map<String, String>, @RequestBody wordData: WordRequestData): ResponseEntity<Void> {        val userId: String = UserUtils.getCurrentUserId()        wordManagementService.saveWord(userId, wordData)        return ResponseEntity.status(HttpStatus.CREATED).build()    }    @GetMapping("/ping")    fun ping(): ResponseEntity<String> {        return ResponseEntity.ok().body("UP")    }}