package xyz.knightj.my_vocab_vault.domainenum class PartsOfSpeech {    NOUN,    PRONOUN,    VERB,    ADJECTIVE,    ADVERB,    PREPOSITION,    CONJUNCTION,    INTERJECTION}data class Meaning(val partsOfSpeech: PartsOfSpeech, val definition: String)data class WordDetails(val id: String,                       val userId: String,                       val word: String,                       val meaning: Meaning,                       val examples: MutableList<String>?)data class WordData(val word: String,                    val meaning: Meaning,                    val examples: MutableList<String>?)