package xyz.knightj.my_vocab_vault.domainimport com.fasterxml.jackson.annotation.JsonIncludeimport software.amazon.awssdk.enhanced.dynamodb.mapper.annotations.DynamoDbBeanimport software.amazon.awssdk.enhanced.dynamodb.mapper.annotations.DynamoDbPartitionKeyimport software.amazon.awssdk.enhanced.dynamodb.mapper.annotations.DynamoDbSortKeyenum class PartsOfSpeech {    NOUN,    PRONOUN,    VERB,    ADJECTIVE,    ADVERB,    PREPOSITION,    CONJUNCTION,    INTERJECTION}data class Meaning(val partsOfSpeech: PartsOfSpeech, val definition: String)data class WordDetails(val id: String,                       val userId: String,                       val word: String,                       val meaning: Meaning,                       val examples: MutableList<String>?)data class WordData(val word: String,                    val meaning: Meaning,                    val examples: MutableList<String>?)@DynamoDbBean@JsonInclude(JsonInclude.Include.NON_NULL)data class WordEntity(@get:DynamoDbPartitionKey var id: String = "",                      @get:DynamoDbSortKey var userId: String = "",                      var word: String? = null,                      var meaning: String? = null,                      var examples: MutableList<String>? = null) {}