package xyz.knightj.my_vocab_vault.modelsenum class PartsOfSpeech {    NOUN,    PRONOUN,    VERB,    ADJECTIVE,    ADVERB,    PREPOSITION,    CONJUNCTION,    INTERJECTION}data class WordDetails(val word: String,                       val tags: MutableSet<String>?,                       val meanings: MutableList<MeaningDetails>,                        val createdAt: String?,                        val updatedAt: String?)data class MeaningDetails(val id: String, val partsOfSpeech: PartsOfSpeech?,    val definition: String?, val examples: MutableSet<String>?)data class WordRequestData(val word: String,                    val partsOfSpeech: PartsOfSpeech,                    val definition: String, val tags: MutableList<String>?,                    val examples: MutableList<String>?)