package xyz.knightj.my_vocab_vault.modelsenum class PartsOfSpeech {    NOUN,    PRONOUN,    VERB,    ADJECTIVE,    ADVERB,    PREPOSITION,    CONJUNCTION,    INTERJECTION}data class WordDetails(val word: String,                       var tags: MutableSet<String>?,                       val meanings: MutableList<MeaningDetails>,                       val createdAt: String?,                       var updatedAt: String?)data class MeaningDetails(val id: String, val partsOfSpeech: PartsOfSpeech?,    val definition: String?, val examples: MutableSet<String>?)data class WordRequestData(val word: String,                    val partsOfSpeech: PartsOfSpeech,                    val definition: String, val tags: MutableList<String>?,                    val examples: MutableList<String>?)data class UpdateWordRequestData(val tags: MutableList<String>?,    val meanings: MutableList<MeaningDetails>?)data class WordsResponseData(val numberOfWords: Int,    val words: List<WordDetails>?)