package xyz.knightj.my_vocab_vault.persistenceimport io.awspring.cloud.dynamodb.DynamoDbTemplateimport org.springframework.beans.factory.annotation.Valueimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configurationimport software.amazon.awssdk.auth.credentials.AwsCredentialsProviderimport software.amazon.awssdk.auth.credentials.DefaultCredentialsProviderimport software.amazon.awssdk.enhanced.dynamodb.DynamoDbEnhancedClientimport software.amazon.awssdk.services.dynamodb.DynamoDbClient@Configurationclass DynamoDbConfig {    @Bean    fun dynamoDbClient(): DynamoDbClient = DynamoDbClient.builder()        .build()    @Bean    fun dynamoDbEnhancedClient(dynamoDbClient: DynamoDbClient): DynamoDbEnhancedClient =        DynamoDbEnhancedClient.builder()            .dynamoDbClient(dynamoDbClient)            .build()    @Bean    fun dynamoDbTemplate(dynamoDbEnhancedClient: DynamoDbEnhancedClient): DynamoDbTemplate = DynamoDbTemplate(dynamoDbEnhancedClient)}