package xyz.knightj.my_vocab_vault.persistence.entitiesimport jakarta.persistence.Embeddableimport java.io.Serializableimport java.util.*@Embeddableclass WordEntityKey : Serializable {    constructor()    constructor(userId: String?, wordName: String?) {        this.userId = userId        this.wordName = wordName    }    var userId: String? = null    var wordName: String? = null    override fun equals(o: Any?): Boolean {        if (this === o) return true        return if (o !is WordEntityKey) false else userId == o.userId && wordName == o.wordName    }    override fun hashCode(): Int {        return Objects.hash(userId, wordName)    }}