package xyz.knightj.my_vocab_vault.serviceimport org.springframework.stereotype.Serviceimport xyz.knightj.my_vocab_vault.domain.WordDataimport xyz.knightj.my_vocab_vault.domain.WordDetailsimport xyz.knightj.my_vocab_vault.domain.WordEntityimport xyz.knightj.my_vocab_vault.persistence.WordManagementClientimport java.util.UUID@Serviceclass WordManagementService(private val wordManagementClient: WordManagementClient) {    fun getAllWords(): List<WordDetails>{        return emptyList()    }    fun saveWord(wordData: WordData): Boolean {        wordManagementClient.saveWord(            WordEntity(id = UUID.randomUUID().toString(), userId = "123", word = wordData.word,                "Test", null))        return true;    }}