CREATE TABLE IF NOT EXISTS words (    id          VARCHAR(36)     PRIMARY KEY,    userId      VARCHAR(36)     NOT NULL,    wordName    VARCHAR(255)    NOT NULL,    createdAt   VARCHAR(30),    updatedAt   VARCHAR(30));CREATE TABLE IF NOT EXISTS meanings (    id              VARCHAR(36)      PRIMARY KEY,    wordId          VARCHAR(36)      NOT NULL REFERENCES words(id) ON DELETE CASCADE,    partsOfSpeech   VARCHAR(36),    definition      TEXT,    examples        VARCHAR(255) ARRAY,    createdAt       VARCHAR(30),    updatedAt       VARCHAR(30));---- Insert sample words--INSERT INTO words (id, userId, wordName, createdAt, updatedAt) VALUES--('word-001', 'user-123', 'serendipity', '2024-01-15T10:30:45.123Z', '2024-01-15T10:30:45.123Z'),--('word-002', 'user-123', 'ephemeral', '2024-01-16T14:22:18.456Z', '2024-01-16T14:22:18.456Z'),--('word-003', 'user-456', 'quintessential', '2024-01-17T09:15:33.789Z', '2024-01-17T09:15:33.789Z'),--('word-004', 'user-123', 'melancholy', '2024-01-18T16:45:12.234Z', '2024-01-18T16:45:12.234Z'),--('word-005', 'user-789', 'eloquent', '2024-01-19T11:30:55.567Z', '2024-01-19T11:30:55.567Z');------ Insert sample meanings for 'serendipity'--INSERT INTO meanings (id, wordId, partsOfSpeech, definition, examples, createdAt, updatedAt) VALUES--('mean-001', 'word-001', 'noun', 'The occurrence and development of events by chance in a happy or beneficial way.', ARRAY['Finding my favorite book at the garage sale was pure serendipity.', 'Their meeting was a beautiful serendipity that changed both their lives.'], '2024-01-15T10:31:22.100Z', '2024-01-15T10:31:22.100Z');------ Insert sample meanings for 'ephemeral'--INSERT INTO meanings (id, wordId, partsOfSpeech, definition, examples, createdAt, updatedAt) VALUES--('mean-002', 'word-002', 'adjective', 'Lasting for a very short time.', ARRAY['The beauty of cherry blossoms is ephemeral, lasting only a few weeks.', 'Fame is often ephemeral in the entertainment industry.'], '